contracts/Intents.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "./Treasury.sol";
import "./MultiSig.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract Intents is MultiSig {
    using SafeERC20 for IERC20;

    struct Intent {
        address user;
        address to;
        uint128 amount; // Tightly packed storage
        uint256 timestamp;
        bool executed;
        uint8 signaturesCount; // Reduced to uint8, assuming no more than 255 signers
    }

    uint256 public immutable threshold; // Immutable saves gas on reads
    uint256 public immutable timeLock;  // Immutable saves gas on reads
    uint256 public nextIntentId; // Intent ID tracker

    mapping(uint256 => Intent) public intents;
    mapping(address => uint256) public lastIntentTimestamp; // Per-owner timestamp tracking
    mapping(uint256 => mapping(address => bool)) public signatures; // Track signatures for each intent

    // Custom Errors
    error IntentAlreadyExecuted();
    error ExecutionTimeNotReached();
    error NotEnoughSignatures();
    error AlreadySigned();
    error AmountMustBeGreaterThanZero();

    // Events
    event IntentCreated(uint256 indexed intentId, address indexed user, address indexed to, uint128 amount, uint256 timestamp);
    event IntentApproved(uint256 indexed intentId, address indexed owner);
    event IntentExecuted(uint256 indexed intentId);

    constructor(
        address _treasury, 
        address[] memory _owners, 
        uint256 _requiredSignatures, 
        uint256 _threshold, 
        uint256 _timeLock
    )
        MultiSig(_treasury, _owners, _requiredSignatures) 
    {
        threshold = _threshold;
        timeLock = _timeLock;
    }  

    function createIntent(address _to, uint128 _amount, uint256 _timestamp) external {
        if (_amount == 0) revert AmountMustBeGreaterThanZero();
        if (_timestamp <= block.timestamp) revert ExecutionTimeNotReached();
        if (block.timestamp < lastIntentTimestamp[msg.sender] + timeLock) revert ExecutionTimeNotReached();

        // Store all fields in one write
        intents[nextIntentId] = Intent({
            user: msg.sender,
            to: _to,
            amount: _amount,
            timestamp: _timestamp,
            executed: false,
            signaturesCount: 0
        });
        
        // Update last intent creation time for sender in one storage operation
        lastIntentTimestamp[msg.sender] = block.timestamp;

        emit IntentCreated(nextIntentId, msg.sender, _to, _amount, _timestamp);

        // Increment intent ID
        unchecked {
            nextIntentId++;
        }
    }

    function approveIntent(uint256 _intentId) external onlyOwner {
        Intent storage intent = intents[_intentId]; // Read once from storage
        if (intent.executed) revert IntentAlreadyExecuted();
        if (block.timestamp < intent.timestamp) revert ExecutionTimeNotReached();
        if (signatures[_intentId][msg.sender]) revert AlreadySigned();

        // Mark approval in one write
        signatures[_intentId][msg.sender] = true;

        // Increment signatures count (gas optimization by using unchecked)
        unchecked {
            intent.signaturesCount++;
        }

        emit IntentApproved(_intentId, msg.sender);
    }

    function executeApprovedIntent(uint256 _intentId) external {
        Intent storage intent = intents[_intentId]; // Load intent once
        if (intent.executed) revert IntentAlreadyExecuted();
        if (block.timestamp < intent.timestamp) revert ExecutionTimeNotReached();

        // Store `amount` and `signaturesCount` in memory to avoid multiple storage reads
        uint128 amount = intent.amount;
        uint8 signaturesCount = intent.signaturesCount;

        // Optimize conditional logic with minimal storage access
        if (signaturesCount < (amount < threshold ? 1 : requiredSignatures)) {
            revert NotEnoughSignatures();
        }

        // Mark as executed (single storage write)
        intent.executed = true;

        // Use token's safeTransfer from Treasury to send tokens
        treasury.transfer(intent.to, amount);

        emit IntentExecuted(_intentId);
    }
}

contracts/MultiSig.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "./Treasury.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract MultiSig {
    using SafeERC20 for Treasury;

    Treasury public immutable treasury; // Immutable for gas savings
    address public custodian; // Custodian address is mutable but is often changed rarely
    address[] public owners;  // Array of owners
    mapping(address => bool) public isOwnerMapping;
    mapping(address => uint256) public ownerIndex; // Stores the owner's index in the owners array
    uint256 public requiredSignatures;

    // Custom Errors
    error NotOwner();
    error NotCustodian();
    error InsufficientOwners(uint256 required);
    error AlreadyAnOwner();
    error NotAnOwner();

    // Events
    event OwnerAdded(address indexed newOwner);
    event OwnerRemoved(address indexed removedOwner);
    event RequiredSignaturesChanged(uint256 newRequiredSignatures);

    modifier onlyOwner() {
        if (!isOwnerMapping[msg.sender]) revert NotOwner();
        _;
    }

    modifier onlyCustodian() {
        if (msg.sender != custodian) revert NotCustodian();
        _;
    }

    constructor(address _treasury, address[] memory _owners, uint256 _requiredSignatures) {
        if (_owners.length < _requiredSignatures) revert InsufficientOwners(_requiredSignatures);
        
        // Use immutable for treasury for gas optimization
        treasury = Treasury(payable(_treasury));
        owners = _owners;
        requiredSignatures = _requiredSignatures;
        custodian = msg.sender;

        // Use unchecked for loop to save gas when incrementing
        unchecked {
            for (uint256 i = 0; i < _owners.length; i++) {
                address owner = _owners[i];
                isOwnerMapping[owner] = true;
                ownerIndex[owner] = i; // Map owner to index
            }
        }
    }

    // Add an owner, callable only by custodian
    function addOwner(address _owner) external onlyCustodian {
        if (isOwnerMapping[_owner]) revert AlreadyAnOwner();
        
        owners.push(_owner);
        isOwnerMapping[_owner] = true;
        ownerIndex[_owner] = owners.length - 1; // Store the index directly
        
        emit OwnerAdded(_owner);
    }

    // Remove an owner, callable only by custodian
    function removeOwner(address _owner) external onlyCustodian {
        if (!isOwnerMapping[_owner]) revert NotAnOwner();

        // Get index of owner to remove
        uint256 index = ownerIndex[_owner];
        uint256 lastIndex = owners.length - 1;

        // If the owner to be removed is not the last one, swap with the last owner
        if (index != lastIndex) {
            address lastOwner = owners[lastIndex];
            owners[index] = lastOwner; // Move last owner to the current index
            ownerIndex[lastOwner] = index; // Update last owner's index
        }

        // Remove the last owner and clear the mappings
        owners.pop(); // Remove last element from owners array
        delete isOwnerMapping[_owner]; // Clear isOwner mapping
        delete ownerIndex[_owner]; // Clear ownerIndex mapping

        emit OwnerRemoved(_owner);
    }

    // Change the required signatures, callable only by custodian
    function changeRequiredSignatures(uint256 _newRequiredSignatures) external onlyCustodian {
        if (_newRequiredSignatures > owners.length) revert InsufficientOwners(_newRequiredSignatures);

        requiredSignatures = _newRequiredSignatures;
        emit RequiredSignaturesChanged(_newRequiredSignatures);
    }

    // Custodian renounces their role, setting custodian to zero address
    function renounceCustodian() external onlyCustodian {
        custodian = address(0); // Sets custodian to the zero address
    }

    // Custodian can be replaced
    function replaceCustodian(address _newCustodian) external onlyCustodian {
        custodian = _newCustodian;
    }
}

contracts/Treasury.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract Treasury {
    using SafeERC20 for IERC20; // Use SafeERC20 for safe token transfers

    IERC20 public immutable token; // Mark as immutable to reduce gas on read

    // Custom Errors
    error NotAuthorized();
    error AmountMustBeGreaterThanZero();
    error InsufficientFunds(uint256 available);
    error InvalidTokenReceived();

    // Events
    event Deposited(address indexed from, uint256 amount);

    constructor(address _token) {
        token = IERC20(_token);
    }

    function deposit(uint256 _amount) external {
        if (_amount == 0) revert AmountMustBeGreaterThanZero(); // Using == to save gas
        token.safeTransferFrom(msg.sender, address(this), _amount);
        emit Deposited(msg.sender, _amount);
    }

    function transfer(address to, uint256 amount) external {
        token.safeTransfer(to, amount); // Use SafeERC20's safeTransfer
    }

    receive() external payable {
        // Reject any Ether sent to the contract
        revert InvalidTokenReceived(); // Always revert with this custom error
    }

    function getBalance() external view returns (uint256) {
        return token.balanceOf(address(this)); // Minimal gas impact, no optimization needed here
    }    
}

contracts/USDC.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract USDC is ERC20Permit, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    constructor() ERC20("USD Coin", "USDC") ERC20Permit("USD Coin") {
        // Assign the deployer as the default admin
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    // Override the decimals function to return 6
    function decimals() public view virtual override returns (uint8) {
        return 6;
    }

    // Mint function that can only be called by an address with the MINTER_ROLE
    function mint(address to, uint256 amount) external onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }
}

